export function populateStatesCities(country: any, populate?: populateType, type?: 'lite'): void {
    if (populate?.states) {
        const states = require(`${type === 'lite' ? PATH_LITE : PATH}/states/${country.code}/index.json`)
        type !== 'lite' && (country.statesCount = states.length)
        country.states = states
        if (populate?.cities) {
            for (const state of states) {
                populateCities(country.code, state, populate.cities, type)
            }
        }
    }
}

export function populateCities(countryCode: countryType, state: any, populate: boolean = false, type?: 'lite'): void {
    if (!populate) return
    const cities = require(`${type === 'lite' ? PATH_LITE : PATH}/cities/${countryCode}/${state.iso}/index.json`)
    type !== 'lite' && (state.citiesCount = cities.length)
    state.cities = cities
}